public class StringDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//declare one int variable
		int a = 100;
		//now declare string variable and assign int value to string variable using type casting
		// here we used valueof method to do this
		String s = String.valueOf(a);
		System.out.println("The int converted to String is " + s);

	}

}
